const sizeItems = document.querySelectorAll('.shopitem__main-size-group-item')
const headerCartCount = document.querySelector('.header-cart__count')
const shopitemMainAddcart = document.querySelector('.shopitem__main-addcart')
const shopitemMainColors = document.querySelectorAll('.shopitem__main-colors-item')

for (var i = 0, len = sizeItems.length; i < len; i++) {
  sizeItems[i].addEventListener('click', function(){
    let sizeItemsActive = document.querySelectorAll('.shopitem__main-size-group-item--active')

    if (sizeItemsActive.length >= 1){
      for (var n = 0, len = sizeItemsActive.length; n < len; n++) {
        sizeItemsActive[n].classList.remove('shopitem__main-size-group-item--active')
      }
    }

    this.classList.add('shopitem__main-size-group-item--active')
    })
}

for (var m = 0, len = shopitemMainColors.length; m < len; m++) {
  shopitemMainColors[m].addEventListener("click", function () {
    let shopitemMainColorsActive = document.querySelectorAll(
      ".shopitem__main-colors-item--active"
    );

    if (shopitemMainColorsActive.length >= 1) {
      for (var k = 0, len = shopitemMainColorsActive.length; k < len; k++) {
        shopitemMainColorsActive[k].classList.remove(
          "shopitem__main-colors-item--active"
        );
      }
    }

    this.classList.add("shopitem__main-colors-item--active");
  });
}
